sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
    adbUtils.validateInputVariables(inputs, outputs);
    adbUtils.validateMandatoryVariables(inputs, ['compartment_id']);
    adbUtils.maptoOutputFromInput(inputs, outputs, ['compartment_id', 'sort_by', 'sort_order', 'infrastructure_type', 'lifecycle_state', 'db_workload', 'db_version', 'is_free_tier', 'display_name', 'is_data_guard_enabled']);
})(inputs, outputs);

