sys_variable_value.value:
(function execute(inputs, outputs) {
  	if (inputs.status_code == '200') {
        try {
          	var data = new Array();
            var document;
            var documents = new Object();
            var response_json = JSON.parse(inputs.response);
            outputs.databases_list = JSON.stringify(response_json);

            for (var i = 0; i < response_json.length; i++) {
                var document = new Object();
                document.label = response_json[i].dbName;
                document.name = response_json[i].id;
                data.push(document);
            }
        } catch (ex) {
            var dummyDoc = new Object();
            dummyDoc.label = '<No Database retrieved>';
            dummyDoc.name = null;
            data.push(dummyDoc);
        } finally {
          documents.data = data;
          outputs.documents = documents;
          outputs.status = "Success";
        }
    } else {
      var errMsgs = new AutonomousDatabaseErrorHandler().documentErrorHandler(inputs.response, inputs.status_code);
      outputs.error_message = errMsgs[0];
      outputs.status = errMsgs[1];
  	}
})(inputs, outputs);

