sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
  	var payload = {};

    adbUtils.validateInputVariables(inputs, outputs);
    adbUtils.validateMandatoryVariables(inputs, ['admin_password', 'compartment_id', 'cpu_core_count', 'data_storage_size_in_tbs', 'database_name', 'clone_type', 'source_id']);

  	if (inputs.admin_password.length < 12 || inputs.admin_password.length > 30)
  		throw new Error("The password must be between 12 and 30 characters long.");

  	if (inputs.cpu_core_count < 1 || inputs.cpu_core_count > 128)
      throw new Error("The The number of OCPU cores must be between 1 and 128.");

  	if (inputs.data_storage_size_in_tbs < 1 || inputs.data_storage_size_in_tbs > 128)
      throw new Error("The The number of OCPU cores must be between 1 and 128.");

  	if (inputs.database_name.length < 1 || inputs.database_name.length > 14)
  		throw new Error("The Database Name must be between 1 and 14 characters long.");

  	payload.adminPassword = inputs.admin_password;
  	payload.compartmentId = inputs.compartment_id;
    payload.cpuCoreCount = parseInt(inputs.cpu_core_count);
  	payload.cloneType = inputs.clone_type;
  	payload.sourceId = inputs.source_id;
    payload.dataStorageSizeInTBs = parseInt(inputs.data_storage_size_in_tbs);
    payload.dbName = inputs.database_name;

  	if (inputs.database_version != "")
    	payload.dbVersion = inputs.database_version;

  	if (inputs.database_workload != "")
    	payload.dbWorkload = inputs.database_workload;

  	if (inputs.display_name != "")
    	payload.displayName = inputs.display_name;

    payload.isAutoScalingEnabled = inputs.is_auto_scaling_enabled;
    payload.isDataGuardEnabled = inputs.is_data_guard_enabled;
    payload.isFreeTier = inputs.is_free_tier;

  	if (inputs.license_model != "")
    	payload.licenseModel = inputs.license_model;
  	payload.source = "DATABASE";

  	outputs.payload = JSON.stringify(payload);
})(inputs, outputs);

