sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == "200") {
    try {
  	  var response_json = JSON.parse(inputs.response);
      outputs.status = "Success";
      outputs.compartment_id = response_json.compartmentId;
      outputs.display_name = response_json.displayName;
      outputs.database_id = response_json.id;
      outputs.database_name = response_json.dbName;
      outputs.database_version = response_json.dbVersion;
      outputs.database_workload = response_json.dbWorkload;
      outputs.lifecycle_state = response_json.lifecycleState;
      outputs.time_created = response_json.timeCreated;
      outputs.cpu_core_count = response_json.cpuCoreCount;
      outputs.data_storage_size_in_tbs = response_json.dataStorageSizeInTBs;
      outputs.license_model = response_json.licenseModel;

      outputs.database_details = JSON.stringify(response_json);
    } catch (ex) {
      throw new Error('JSON Parse Error. Response Body may be empty.');
    }
  } else {
    var errMsgs = new AutonomousDatabaseErrorHandler().documentErrorHandler(inputs.response, inputs.status_code);
    outputs.error_message = errMsgs[0];
    outputs.status = errMsgs[1];
  }
})(inputs, outputs);

