sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
    adbUtils.validateInputVariables(inputs, outputs);

  	if (inputs.database_id && inputs.compartment_id)
      throw new Error("Provide only one of these parameters – Database ID, Compartment ID.");

    adbUtils.maptoOutputFromInput(inputs, outputs, ['database_id', 'compartment_id', 'sort_by', 'sort_order', 'lifecycle_state', 'display_name']);
})(inputs, outputs);

