sys_variable_value.value:
(function execute(inputs, outputs) {
	var adbUtils = new AutonomousDatabaseSpokeUtils();
  	var payload = {};

    adbUtils.validateInputVariables(inputs, outputs);
    adbUtils.validateMandatoryVariables(inputs, ['database_id']);

  	if (inputs.adminPassword && (inputs.adminPassword.length < 12 || inputs.adminPassword.length > 30))
  		throw new Error("The Admin Password must be between 12 and 30 characters long.");

  	if (inputs.cpuCoreCount && (inputs.cpuCoreCount < 1 || inputs.cpuCoreCount > 128))
      	throw new Error("The The number of OCPU cores must be between 1 and 128.");

  	if (inputs.dataStorageSizeInTBs && (inputs.dataStorageSizeInTBs < 1 || inputs.dataStorageSizeInTBs > 128))
      	throw new Error("The The number of OCPU cores must be between 1 and 128.");

  	if (inputs.dbName && (inputs.dbName.length < 1 || inputs.dbName.length > 14))
  		throw new Error("The Database Name must be between 1 and 14 characters long.");

  	for (key in inputs){
    	if (key != 'database_id'){
        	if (inputs[key] != "")
              payload[key] = inputs[key];
        }
    }

  	outputs.database_id = inputs.database_id;
  	outputs.payload = JSON.stringify(payload);
})(inputs, outputs);

